import { Pane, Heading, Paragraph } from 'evergreen-ui'
import formatDistance from 'date-fns/formatDistance'
import { useEffect, useState } from 'react'
import Head from 'next/head'

const client = require('contentful').createClient({
  space: process.env.CONTENTFUL_SPACE_ID,
  accessToken: process.env.CONTENTFUL_ACCESS_TOKEN
})

function HomePage() {
  async function fetchContentTypes() {
    const types = await client.getContentTypes()
    if (types.items) return types.items
    console.log('Error getting Content Types.')
  }

  async function fetchEntriesForContentType(contentType) {
    const entries = await client.getEntries({
      content_type: contentType.sys.id
    })

    if (entries.items) return entries.items
    console.log(`Error getting Entries for ${contentType.name}.`)
  }

  const [posts, setPosts] = useState([])

  useEffect(() => {
    async function getPosts() {
      const contentTypes = await fetchContentTypes()
      const allPosts = await fetchEntriesForContentType(contentTypes[0])
      setPosts([...allPosts])
    }
    getPosts()
  }, [])

  if (posts.length > 0) console.log(posts[0].fields)

  return (
    <>
      <Head>
        <title>Next.js + Contentful</title>
        <link href="https://fonts.googleapis.com/css?family=EB+Garamond&display=swap" rel="stylesheet" />

      </Head>

      <Pane
        padding={40}
        marginX="auto"
        maxWidth={600}
      >
      {posts.length > 0
          ? posts.map(p => (
            <Pane
              marginBottom={32}
            >
              <Heading
                color="black"
                size={800}
                fontFamily="EB Garamond"
                fontWeight={600}
              >
                {p.fields.title}
              </Heading>
              <Paragraph
                marginY={8}
                fontFamily="EB Garamond"
                fontSize={20}
              >
                {p.fields.description}
              </Paragraph>
              <Pane>
                <Paragraph
                  marginTop={8}
                  fontFamily="EB Garamond"
                  fontSize={16}
                >
                  by {p.fields.author.fields.name} {formatDistance(new Date(p.fields.publishDate), new Date(), { addSuffix: true })}
                </Paragraph>

              </Pane>
          </Pane>
        ))
        : null}
      </Pane>
    </>
  )

}

export default HomePage
